import { FormControl, FormGroup } from '@angular/forms';
import * as i0 from "@angular/core";
import * as i1 from "@angular/material/dialog";
import * as i2 from "ngx-toastr";
import * as i3 from "../services/booking/booking.service";
import * as i4 from "../services/citas-usuarios/citas-usuarios.service";
import * as i5 from "@angular/forms";
export class ModalRegistrarCitasComponent {
    constructor(dialogRef, toastrSvc, booking, users) {
        this.dialogRef = dialogRef;
        this.toastrSvc = toastrSvc;
        this.booking = booking;
        this.users = users;
        this.bookingData = new FormGroup({
            phone: new FormControl(''),
            fecha: new FormControl(''),
            hora: new FormControl('')
        });
    }
    ngOnInit() {
        this.bookingData.value.fecha = this.fecha;
    }
    submitBooking() {
        this.users.saveUserByPhone({ phone: this.bookingData.value.phone }).subscribe(async (res) => {
            if (res != null) {
                let generateBooking = {
                    userId: Object.values(res)[0],
                    bookingDate: new Date(this.bookingData.value.fecha)
                };
                var d = new Date(this.bookingData.value.fecha + '' + this.bookingData.value.hora);
                console.log(d);
                this.booking.saveBooking(generateBooking).subscribe(res => {
                    console.log(res);
                });
            }
        });
    }
    closeModal() {
        this.dialogRef.close();
    }
}
